import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Register from "./pages/Register";
import Login from "./pages/Login";
import UserDashboard from "./pages/UserDashboard"; // User Page
import WorkerDashboard from "./pages/WorkerDashboard"; // Worker Page
import Home from "./pages/Home"; // Landing Page
import RoleSelection from "./pages/RoleSelection"; // Worker or User selection

function App() {
  return (
    <Router>
      <Routes>
        {/* Home Page */}
        <Route path="/" element={<Home />} />

        {/* Role Selection Before Login */}
        <Route path="/select-role" element={<RoleSelection />} />

        {/* Authentication Pages */}
        <Route path="/register" element={<Register />} />
        <Route path="/login" element={<Login />} />

        {/* Redirect to respective dashboards after login */}
        <Route path="/user-dashboard" element={<UserDashboard />} />
        <Route path="/worker-dashboard" element={<WorkerDashboard />} />
      </Routes>
    </Router>
  );
}

export default App;
