import React, { useState } from "react";
import { signInWithEmailAndPassword, signInWithPopup, RecaptchaVerifier, signInWithPhoneNumber } from "firebase/auth";
import { auth, googleProvider } from "../firebaseConfig";
import { Link, useNavigate } from "react-router-dom";
import "../styles/Login.css";
import logo from "../assets/logo.png";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [phone, setPhone] = useState("");
  const [otp, setOtp] = useState("");
  const [role, setRole] = useState(null); // Track role selection
  const [showRolePopup, setShowRolePopup] = useState(true);
  const [otpSent, setOtpSent] = useState(false);
  const navigate = useNavigate();

  // Select Worker or User
  const handleRoleSelection = (selectedRole) => {
    setRole(selectedRole);
    setShowRolePopup(false);
  };

  // Email & Password Login
  const handleEmailLogin = async (e) => {
    e.preventDefault();
    try {
      await signInWithEmailAndPassword(auth, email, password);
      navigate(role === "user" ? "/user-dashboard" : "/worker-dashboard");
    } catch (error) {
      console.error("Login Error:", error);
    }
  };

  // Google Login
  const handleGoogleLogin = async () => {
    try {
      await signInWithPopup(auth, googleProvider);
      navigate(role === "user" ? "/user-dashboard" : "/worker-dashboard");
    } catch (error) {
      console.error("Google Login Error:", error);
    }
  };

  // Send OTP
  const sendOtp = async () => {
    if (!phone) return alert("Enter a valid phone number");

    try {
      window.recaptchaVerifier = new RecaptchaVerifier(auth, "recaptcha-container", {
        size: "invisible",
      });

      const confirmationResult = await signInWithPhoneNumber(auth, phone, window.recaptchaVerifier);
      window.confirmationResult = confirmationResult;
      setOtpSent(true);
      alert("OTP sent to your phone");
    } catch (error) {
      console.error("OTP Send Error:", error);
    }
  };

  // Verify OTP
  const verifyOtp = async () => {
    if (!otp) return alert("Enter OTP");

    try {
      await window.confirmationResult.confirm(otp);
      navigate(role === "user" ? "/user-dashboard" : "/worker-dashboard");
    } catch (error) {
      console.error("OTP Verification Error:", error);
    }
  };

  return (
    <div className="login-container">
      {/* Background Video */}
      <video autoPlay muted loop className="login-video">
        <source src="/background.mp4" type="video/mp4" />
      </video>

      {/* Role Selection Popup */}
      {showRolePopup && (
        <div className="role-popup">
          <h2>Select Your Role</h2>
          <button onClick={() => handleRoleSelection("user")}>User</button>
          <button onClick={() => handleRoleSelection("worker")}>Worker</button>
        </div>
      )}

      {!showRolePopup && (
        <div className="login-box">
          <img src={logo} alt="Knock Nok" className="login-logo" />
          <h2>Welcome to Knock Nok</h2>
          <p>Login as {role.charAt(0).toUpperCase() + role.slice(1)}</p>

          {/* Email & Password Login */}
          <form className="login-form" onSubmit={handleEmailLogin}>
            <input type="email" placeholder="Email" required value={email} onChange={(e) => setEmail(e.target.value)} />
            <input type="password" placeholder="Password" required value={password} onChange={(e) => setPassword(e.target.value)} />
            <button type="submit">Sign In</button>
          </form>

          <div className="divider">OR</div>

          {/* Google Sign In */}
          <button className="google-btn" onClick={handleGoogleLogin}>Sign In with Google</button>

          <div className="divider">OR</div>

          {/* Phone Number Login */}
          {!otpSent ? (
            <>
              <input type="text" placeholder="Enter Mobile Number" value={phone} onChange={(e) => setPhone(e.target.value)} />
              <button className="otp-btn" onClick={sendOtp}>Send OTP</button>
            </>
          ) : (
            <>
              <input type="text" placeholder="Enter OTP" value={otp} onChange={(e) => setOtp(e.target.value)} />
              <button className="otp-btn" onClick={verifyOtp}>Verify OTP</button>
            </>
          )}

          <p>Don't have an account? <Link to="/register">Register Now</Link></p>
        </div>
      )}

      {/* Invisible Recaptcha for OTP */}
      <div id="recaptcha-container"></div>
    </div>
  );
};

export default Login;
