import React, { useState } from "react";
import { auth } from "../firebaseConfig";
import { createUserWithEmailAndPassword } from "firebase/auth";
import { useNavigate } from "react-router-dom";
import "../styles/Register.css";

const Register = () => {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    profileImage: null,
    mobileNumber: "",
    altMobile: "",
    email: "",
    aadhar: "",
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await createUserWithEmailAndPassword(auth, formData.email, "defaultPassword");
      navigate("/login");
    } catch (error) {
      console.error("Registration error:", error.message);
    }
  };

  return (
    <div className="register-container">
      <div className="register-box">
        <h2>Register</h2>
        <form onSubmit={handleSubmit}>
          <input type="text" name="firstName" placeholder="First Name" required onChange={handleChange} />
          <input type="text" name="lastName" placeholder="Last Name" required onChange={handleChange} />
          <input type="file" name="profileImage" accept="image/*" required />
          <input type="tel" name="mobileNumber" placeholder="Mobile Number" required onChange={handleChange} />
          <input type="tel" name="altMobile" placeholder="Alternative Mobile (Optional)" onChange={handleChange} />
          <input type="email" name="email" placeholder="Email (Optional)" onChange={handleChange} />
          <input type="text" name="aadhar" placeholder="Aadhar Card Number" required onChange={handleChange} />
          <button type="submit">Register</button>
        </form>
        <p>Already have an account? <a href="/login">Login</a></p>
      </div>
    </div>
  );
};

export default Register;
