import React, { useState } from "react";
import { auth, googleProvider } from "../firebaseConfig";
import { signInWithPopup, signInWithEmailAndPassword, RecaptchaVerifier, signInWithPhoneNumber } from "firebase/auth";
import { useNavigate } from "react-router-dom";
import "../styles/Login.css";
import logo from "../assets/logo.png";
import { FcGoogle } from "react-icons/fc";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [phone, setPhone] = useState("");
  const [otp, setOtp] = useState("");
  const [userType, setUserType] = useState("user");
  const [confirmationResult, setConfirmationResult] = useState(null);
  const navigate = useNavigate();

  const handleGoogleLogin = async () => {
    try {
      const result = await signInWithPopup(auth, googleProvider);
      navigate(userType === "worker" ? "/worker-dashboard" : "/user-dashboard");
    } catch (error) {
      console.error("Google Sign-In Error", error);
    }
  };

  const handleEmailLogin = async () => {
    try {
      await signInWithEmailAndPassword(auth, email, password);
      navigate(userType === "worker" ? "/worker-dashboard" : "/user-dashboard");
    } catch (error) {
      console.error("Email Login Error", error);
    }
  };

  const sendOtp = async () => {
    try {
      const recaptcha = new RecaptchaVerifier(auth, "recaptcha-container", { size: "invisible" });
      const result = await signInWithPhoneNumber(auth, phone, recaptcha);
      setConfirmationResult(result);
      alert("OTP Sent!");
    } catch (error) {
      console.error("OTP Error", error);
    }
  };

  const verifyOtp = async () => {
    try {
      await confirmationResult.confirm(otp);
      navigate(userType === "worker" ? "/worker-dashboard" : "/user-dashboard");
    } catch (error) {
      console.error("OTP Verification Error", error);
    }
  };

  return (
    <div className="login-container">
      <img src={logo} alt="Knock Nok Logo" className="logo" />
      <h2>Welcome to Knock Nok</h2>
      <p>Please log in to access features.</p>
      
      <select onChange={(e) => setUserType(e.target.value)} className="role-selector">
        <option value="user">User</option>
        <option value="worker">Worker</option>
      </select>
      
      <input type="email" placeholder="Email" onChange={(e) => setEmail(e.target.value)} />
      <input type="password" placeholder="Password" onChange={(e) => setPassword(e.target.value)} />
      <button onClick={handleEmailLogin}>Sign in with Email</button>
      
      <hr />
      
      <button onClick={handleGoogleLogin} className="google-btn">
        <FcGoogle /> Sign in with Google
      </button>
      
      <hr />
      
      <input type="text" placeholder="Phone Number" onChange={(e) => setPhone(e.target.value)} />
      <button onClick={sendOtp}>Send OTP</button>
      <input type="text" placeholder="Enter OTP" onChange={(e) => setOtp(e.target.value)} />
      <button onClick={verifyOtp}>Verify OTP</button>
      
      <div id="recaptcha-container"></div>
      
      <p>Don't have an account? <a href="/register">Register now</a></p>
    </div>
  );
};

export default Login;
