import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { auth, googleProvider } from "../firebaseConfig";
import { signInWithPopup, signInWithEmailAndPassword } from "firebase/auth";
import "../styles/Login.css"; // Ensure this file exists and is properly linked
import logo from "../assets/logo.png"; // Ensure the logo file is correctly placed
import { FcGoogle } from "react-icons/fc"; // Google icon for better UI

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    if (!role) {
      setError("⚠️ Please select a role before logging in.");
      return;
    }
    try {
      await signInWithEmailAndPassword(auth, email, password);
      navigate(role === "user" ? "/user-dashboard" : "/worker-dashboard");
    } catch (error) {
      setError(error.message);
    }
  };

  const handleGoogleLogin = async () => {
    if (!role) {
      setError("⚠️ Please select a role before logging in.");
      return;
    }
    try {
      await signInWithPopup(auth, googleProvider);
      navigate(role === "user" ? "/user-dashboard" : "/worker-dashboard");
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <img src={logo} alt="Knock Nok Logo" className="logo" />
        <h2>Welcome to <span>Knock Nok</span></h2>
        <p className="subtitle">Please log in to access features.</p>

        {error && <p className="error-message">{error}</p>}

        <div className="role-selection">
          <button
            className={role === "user" ? "role-btn selected" : "role-btn"}
            onClick={() => setRole("user")}
          >
            User
          </button>
          <button
            className={role === "worker" ? "role-btn selected" : "role-btn"}
            onClick={() => setRole("worker")}
          >
            Worker
          </button>
        </div>

        <form onSubmit={handleLogin}>
          <input
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Enter your password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit" className="login-btn">Login</button>
        </form>

        <button onClick={handleGoogleLogin} className="google-btn">
          <FcGoogle size={22} /> Sign in with Google
        </button>

        <p className="register-link">
          Don't have an account? <a href="/register">Register here</a>
        </p>
      </div>
    </div>
  );
};

export default Login;
