import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../styles/Register.css"; // Ensure the CSS file is linked

const Register = () => {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    profileImage: null,
    mobileNumber: "",
    altMobile: "",
    email: "",
    aadharNumber: "",
  });

  const handleChange = (e) => {
    const { name, value, type, files } = e.target;
    setFormData({
      ...formData,
      [name]: type === "file" ? files[0] : value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("User Registered:", formData);
  };

  return (
    <div className="register-container">
      <div className="register-box">
        <h2>Register</h2>
        <form onSubmit={handleSubmit}>
          <div className="input-group">
            <label>First Name</label>
            <input type="text" name="firstName" required onChange={handleChange} />
          </div>
          <div className="input-group">
            <label>Last Name</label>
            <input type="text" name="lastName" required onChange={handleChange} />
          </div>
          <div className="input-group">
            <label>Profile Image</label>
            <input type="file" name="profileImage" accept="image/*" onChange={handleChange} />
          </div>
          <div className="input-group">
            <label>Mobile Number</label>
            <input type="tel" name="mobileNumber" required onChange={handleChange} />
          </div>
          <div className="input-group">
            <label>Alternative Mobile (Optional)</label>
            <input type="tel" name="altMobile" onChange={handleChange} />
          </div>
          <div className="input-group">
            <label>Email (Optional)</label>
            <input type="email" name="email" onChange={handleChange} />
          </div>
          <div className="input-group">
            <label>Aadhar Card Number</label>
            <input type="text" name="aadharNumber" required onChange={handleChange} />
          </div>
          <button type="submit">Register</button>
        </form>
        <p>
          Already have an account? <Link to="/login">Login</Link>
        </p>
      </div>
    </div>
  );
};

export default Register;
