import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/Register.css";

const Register = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    profileImage: "",
    mobileNumber: "",
    altMobile: "",
    email: "",
    aadhar: "",
    userType: "user",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Registering:", formData);
    // Redirect user based on type
    navigate(formData.userType === "worker" ? "/worker-dashboard" : "/user-dashboard");
  };

  return (
    <div className="register-container">
      <div className="register-box">
        <h2>Create Your Account</h2>
        <form onSubmit={handleSubmit}>
          <div className="input-group">
            <label>First Name</label>
            <input type="text" name="firstName" required onChange={handleChange} />
          </div>
          <div className="input-group">
            <label>Last Name</label>
            <input type="text" name="lastName" required onChange={handleChange} />
          </div>
          <div className="input-group">
            <label>Profile Image</label>
            <input type="file" accept="image/*" onChange={handleChange} />
          </div>
          <div className="input-group">
            <label>Mobile Number</label>
            <input type="tel" name="mobileNumber" required onChange={handleChange} />
          </div>
          <div className="input-group">
            <label>Alternative Mobile (Optional)</label>
            <input type="tel" name="altMobile" onChange={handleChange} />
          </div>
          <div className="input-group">
            <label>Email (Optional)</label>
            <input type="email" name="email" onChange={handleChange} />
          </div>
          <div className="input-group">
            <label>Aadhar Card Number</label>
            <input type="text" name="aadhar" required onChange={handleChange} />
          </div>
          <div className="input-group">
            <label>User Type</label>
            <select name="userType" onChange={handleChange}>
              <option value="user">User</option>
              <option value="worker">Worker</option>
            </select>
          </div>
          <button type="submit">Register</button>
        </form>
        <p>Already have an account? <a href="/login">Sign In</a></p>
      </div>
    </div>
  );
};

export default Register;
