import React, { useState } from "react";
import { auth, googleProvider, phoneProvider } from "../firebaseConfig";
import { signInWithPopup, signInWithEmailAndPassword, RecaptchaVerifier, signInWithPhoneNumber } from "firebase/auth";
import "../styles/Login.css";
import { FcGoogle } from "react-icons/fc";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [phone, setPhone] = useState("");
  const [otp, setOtp] = useState("");
  const [verificationId, setVerificationId] = useState(null);
  const [isOtpSent, setIsOtpSent] = useState(false);

  // Email & Password Login
  const handleEmailLogin = async (e) => {
    e.preventDefault();
    try {
      await signInWithEmailAndPassword(auth, email, password);
      alert("Login Successful");
    } catch (error) {
      alert(error.message);
    }
  };

  // Google Login
  const handleGoogleLogin = async () => {
    try {
      await signInWithPopup(auth, googleProvider);
      alert("Google Login Successful");
    } catch (error) {
      alert(error.message);
    }
  };

  // Mobile Number OTP Login
  const handleSendOtp = async () => {
    try {
      window.recaptchaVerifier = new RecaptchaVerifier(auth, 'recaptcha-container', { size: 'invisible' });
      const confirmationResult = await signInWithPhoneNumber(auth, phone, window.recaptchaVerifier);
      setVerificationId(confirmationResult);
      setIsOtpSent(true);
      alert("OTP Sent");
    } catch (error) {
      alert(error.message);
    }
  };

  const handleVerifyOtp = async () => {
    try {
      await verificationId.confirm(otp);
      alert("Phone Login Successful");
    } catch (error) {
      alert(error.message);
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>Welcome to Knock Nok</h2>
        <p>Please log in to access features.</p>
        
        {/* Email & Password Login */}
        <form onSubmit={handleEmailLogin} className="login-form">
          <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
          <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} required />
          <button type="submit">Sign in</button>
        </form>
        
        <div className="divider">OR</div>
        
        {/* Google Login */}
        <button className="google-btn" onClick={handleGoogleLogin}>
          <FcGoogle /> Sign in with Google
        </button>
        
        <div className="divider">OR</div>
        
        {/* Mobile Number Login */}
        <div className="phone-login">
          <input type="tel" placeholder="Enter mobile number" value={phone} onChange={(e) => setPhone(e.target.value)} required />
          <button onClick={handleSendOtp}>Send OTP</button>
          {isOtpSent && (
            <>
              <input type="text" placeholder="Enter OTP" value={otp} onChange={(e) => setOtp(e.target.value)} required />
              <button onClick={handleVerifyOtp}>Verify OTP</button>
            </>
          )}
        </div>
        <div id="recaptcha-container"></div>
      </div>
    </div>
  );
};

export default Login;
