import React, { useState } from "react";
import { signInWithEmailAndPassword, signInWithPopup } from "firebase/auth";
import { auth, googleProvider } from "../firebaseConfig";
import { Link, useNavigate } from "react-router-dom";
import "../styles/Login.css";
import logo from "../assets/logo.png";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState(null); // To track selected role
  const [showRolePopup, setShowRolePopup] = useState(true);
  const navigate = useNavigate();

  // Function to select Worker or User
  const handleRoleSelection = (selectedRole) => {
    setRole(selectedRole);
    setShowRolePopup(false); // Close popup after selection
  };

  // Email & Password Login
  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      await signInWithEmailAndPassword(auth, email, password);
      if (role === "user") {
        navigate("/user-dashboard");
      } else {
        navigate("/worker-dashboard");
      }
    } catch (error) {
      console.error("Login Error:", error);
    }
  };

  // Google Login
  const handleGoogleLogin = async () => {
    try {
      await signInWithPopup(auth, googleProvider);
      if (role === "user") {
        navigate("/user-dashboard");
      } else {
        navigate("/worker-dashboard");
      }
    } catch (error) {
      console.error("Google Login Error:", error);
    }
  };

  return (
    <div className="login-container">
      {/* Background Video */}
      <video autoPlay muted loop className="login-video">
        <source src="/background.mp4" type="video/mp4" />
        Your browser does not support the video tag.
      </video>

      {/* Role Selection Popup */}
      {showRolePopup && (
        <div className="role-popup">
          <h2>Select Your Role</h2>
          <button onClick={() => handleRoleSelection("user")}>User</button>
          <button onClick={() => handleRoleSelection("worker")}>Worker</button>
        </div>
      )}

      {!showRolePopup && (
        <div className="login-box">
          <img src={logo} alt="Knock Nok" className="login-logo" />
          <h2>Welcome to Knock Nok</h2>
          <p>Login as {role.charAt(0).toUpperCase() + role.slice(1)}</p>

          {/* Email & Password Login Form */}
          <form className="login-form" onSubmit={handleLogin}>
            <input
              type="email"
              placeholder="Email"
              required
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <input
              type="password"
              placeholder="Password"
              required
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button type="submit">Sign In</button>
          </form>

          <div className="divider">OR</div>

          {/* Google Sign In */}
          <button className="google-btn" onClick={handleGoogleLogin}>
            Sign In with Google
          </button>

          <div className="divider">OR</div>

          {/* Phone Number Login */}
          <Link to="/phone-login">
            <button className="google-btn">Sign In with Mobile Number</button>
          </Link>

          <p>
            Don't have an account? <Link to="/register">Register Now</Link>
          </p>
        </div>
      )}
    </div>
  );
};

export default Login;
