import React, { useState } from 'react';
import { auth, googleProvider } from '../firebaseConfig';
import { signInWithPopup, signInWithEmailAndPassword } from 'firebase/auth';
import { useNavigate } from 'react-router-dom';
import '../styles/Login.css';
import logo from '../assets/logo.png';
import { FcGoogle } from 'react-icons/fc';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleEmailLogin = async (e) => {
        e.preventDefault();
        try {
            await signInWithEmailAndPassword(auth, email, password);
            navigate('/dashboard');
        } catch (error) {
            console.error(error);
        }
    };

    const handleGoogleLogin = async () => {
        try {
            await signInWithPopup(auth, googleProvider);
            navigate('/dashboard');
        } catch (error) {
            console.error(error);
        }
    };

    return (
        <div className="login-container">
            <div className="video-background">
                <iframe 
                    src="https://www.youtube.com/embed/RK1RRVR9A2g?autoplay=1&mute=1&loop=1&playlist=RK1RRVR9A2g" 
                    frameBorder="0" 
                    allow="autoplay; fullscreen" 
                    title="Background Video"
                ></iframe>
            </div>
            <div className="login-box">
                <img src={logo} alt="Knock Nok Logo" className="login-logo" />
                <h2>Welcome to Knock Nok</h2>
                <p>Please log in to access features.</p>
                <form onSubmit={handleEmailLogin}>
                    <input 
                        type="email" 
                        placeholder="Email" 
                        value={email} 
                        onChange={(e) => setEmail(e.target.value)} 
                        required
                    />
                    <input 
                        type="password" 
                        placeholder="Password" 
                        value={password} 
                        onChange={(e) => setPassword(e.target.value)} 
                        required
                    />
                    <button type="submit">Sign In</button>
                </form>
                <button className="google-login" onClick={handleGoogleLogin}>
                    <FcGoogle size={24} /> Sign in with Google
                </button>
                <p>Don't have an account? <a href="/register">Register Now</a></p>
            </div>
        </div>
    );
};

export default Login;
