import React, { useState } from "react";
import { auth, googleProvider } from "../firebaseConfig";
import { signInWithPopup, signInWithEmailAndPassword } from "firebase/auth";
import "../styles/Login.css";
import logo from "../assets/logo.png";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [phone, setPhone] = useState("");
  const [otp, setOtp] = useState("");
  const [role, setRole] = useState("user");

  const handleEmailLogin = async (e) => {
    e.preventDefault();
    try {
      await signInWithEmailAndPassword(auth, email, password);
      alert("Login Successful!");
    } catch (error) {
      alert(error.message);
    }
  };

  const handleGoogleLogin = async () => {
    try {
      await signInWithPopup(auth, googleProvider);
      alert("Google Login Successful!");
    } catch (error) {
      alert(error.message);
    }
  };

  const handleOTPLogin = () => {
    alert("OTP Verification Flow Placeholder");
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <img src={logo} alt="Knock Nok" className="logo" />
        <h2>Welcome to Knock Nok</h2>
        <p>Please log in to access features.</p>
        <div className="role-selection">
          <label>
            <input
              type="radio"
              name="role"
              value="user"
              checked={role === "user"}
              onChange={() => setRole("user")}
            />
            User
          </label>
          <label>
            <input
              type="radio"
              name="role"
              value="worker"
              checked={role === "worker"}
              onChange={() => setRole("worker")}
            />
            Worker
          </label>
        </div>
        <form onSubmit={handleEmailLogin}>
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit" className="login-btn">Sign In</button>
        </form>
        <button onClick={handleGoogleLogin} className="google-login">Sign in with Google</button>
        <div className="otp-section">
          <input
            type="tel"
            placeholder="Phone Number"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
          />
          <button onClick={handleOTPLogin} className="otp-btn">Send OTP</button>
          <input
            type="text"
            placeholder="Enter OTP"
            value={otp}
            onChange={(e) => setOtp(e.target.value)}
          />
          <button onClick={handleOTPLogin} className="otp-btn">Verify OTP</button>
        </div>
        <p>New here? <a href="/register" className="register-link">Register Now</a></p>
      </div>
    </div>
  );
};

export default Login;
