import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { auth, googleProvider } from "../firebaseConfig";
import { signInWithPopup, signInWithEmailAndPassword } from "firebase/auth";
import "../styles/Login.css"; // Ensure this file exists and is properly linked
import logo from "../assets/logo.png"; // Ensure the logo file is correctly placed

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    if (!role) {
      alert("Please select a role before logging in.");
      return;
    }
    try {
      await signInWithEmailAndPassword(auth, email, password);
      if (role === "user") {
        navigate("/user-dashboard");
      } else {
        navigate("/worker-dashboard");
      }
    } catch (error) {
      alert(error.message);
    }
  };

  const handleGoogleLogin = async () => {
    if (!role) {
      alert("Please select a role before logging in.");
      return;
    }
    try {
      await signInWithPopup(auth, googleProvider);
      if (role === "user") {
        navigate("/user-dashboard");
      } else {
        navigate("/worker-dashboard");
      }
    } catch (error) {
      alert(error.message);
    }
  };

  return (
    <div className="login-container">
      <img src={logo} alt="Knock Nok Logo" className="logo" />
      <h2>Welcome to Knock Nok</h2>
      <p>Please log in to access features.</p>
      <div className="role-selection">
        <label>
          <input
            type="radio"
            value="user"
            checked={role === "user"}
            onChange={() => setRole("user")}
          />
          User
        </label>
        <label>
          <input
            type="radio"
            value="worker"
            checked={role === "worker"}
            onChange={() => setRole("worker")}
          />
          Worker
        </label>
      </div>
      <form onSubmit={handleLogin}>
        <input
          type="email"
          placeholder="Enter your email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Enter your password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
      </form>
      <button onClick={handleGoogleLogin} className="google-btn">
        Sign in with Google
      </button>
      <p>
        Don't have an account? <a href="/register">Register here</a>
      </p>
    </div>
  );
};

export default Login;
