import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { auth, googleProvider } from "../firebaseConfig";
import { signInWithPopup, signInWithEmailAndPassword } from "firebase/auth";
import "../styles/Login.css";
import logo from "../assets/logo.png"; // Ensure this file exists

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    if (!role) {
      alert("Please select a role: User or Worker");
      return;
    }
    try {
      await signInWithEmailAndPassword(auth, email, password);
      navigate(role === "user" ? "/user-dashboard" : "/worker-dashboard");
    } catch (error) {
      alert(error.message);
    }
  };

  const handleGoogleLogin = async () => {
    if (!role) {
      alert("Please select a role before logging in");
      return;
    }
    try {
      await signInWithPopup(auth, googleProvider);
      navigate(role === "user" ? "/user-dashboard" : "/worker-dashboard");
    } catch (error) {
      alert(error.message);
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <img src={logo} alt="Knock Nok Logo" className="logo" />
        <h2>Welcome to Knock Nok</h2>
        <p>Please log in to access features.</p>
        <div className="role-selection">
          <button
            className={role === "user" ? "active" : ""}
            onClick={() => setRole("user")}
          >
            Login as User
          </button>
          <button
            className={role === "worker" ? "active" : ""}
            onClick={() => setRole("worker")}
          >
            Login as Worker
          </button>
        </div>
        <form onSubmit={handleLogin}>
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit">Login</button>
        </form>
        <button className="google-login" onClick={handleGoogleLogin}>
          Sign in with Google
        </button>
        <p>
          Don't have an account? <a href="/register">Register Here</a>
        </p>
      </div>
    </div>
  );
};

export default Login;
