// src/App.js
import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import Login from './components/Login';
import Home from './components/Home';
import Dashboard from './components/Dashboard';
import Booking from './components/Booking';
import Services from './components/Services';

function App() {
    const [userType, setUserType] = useState(null); // 'user' or 'worker'
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    useEffect(() => {
        const storedUser = localStorage.getItem('userType');
        if (storedUser) {
            setUserType(storedUser);
            setIsLoggedIn(true);
        }
    }, []);

    return (
        <>
            {isLoggedIn && <Navbar setIsLoggedIn={setIsLoggedIn} />}
            <Routes>
                <Route path="/login" element={<Login setIsLoggedIn={setIsLoggedIn} setUserType={setUserType} />} />
                <Route path="/" element={isLoggedIn ? <Home userType={userType} /> : <Navigate to="/login" />} />
                <Route path="/dashboard" element={isLoggedIn ? <Dashboard userType={userType} /> : <Navigate to="/login" />} />
                <Route path="/booking" element={isLoggedIn ? <Booking userType={userType} /> : <Navigate to="/login" />} />
                <Route path="/services" element={isLoggedIn ? <Services userType={userType} /> : <Navigate to="/login" />} />
            </Routes>
        </>
    );
}

export default App;
