import { useState } from "react";
import { signInWithEmailAndPassword, signInWithPopup } from "firebase/auth";
import { auth, googleProvider } from "../firebaseConfig";
import { useNavigate } from "react-router-dom";
import "./Auth.css";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("user"); // Default role selection
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;
      console.log("Login successful:", user);
      
      // Redirect based on role
      if (role === "user") {
        navigate("/user-dashboard");
      } else {
        navigate("/worker-dashboard");
      }
    } catch (error) {
      setError("Invalid email or password. Try again.");
    }
  };

  const handleGoogleLogin = async () => {
    try {
      const result = await signInWithPopup(auth, googleProvider);
      console.log("Google login successful:", result.user);
      
      // Redirect based on role selection
      if (role === "user") {
        navigate("/user-dashboard");
      } else {
        navigate("/worker-dashboard");
      }
    } catch (error) {
      setError("Google Sign-In failed.");
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-box">
        <h2>Login to Knock Nok</h2>
        {error && <p className="error">{error}</p>}

        <form onSubmit={handleLogin}>
          <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
          <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} required />
          
          {/* Role Selection */}
          <div className="role-selection">
            <label>
              <input type="radio" name="role" value="user" checked={role === "user"} onChange={() => setRole("user")} />
              User
            </label>
            <label>
              <input type="radio" name="role" value="worker" checked={role === "worker"} onChange={() => setRole("worker")} />
              Worker
            </label>
          </div>

          <button type="submit">Login</button>
        </form>

        <button className="google-btn" onClick={handleGoogleLogin}>Sign in with Google</button>

        <p>Don't have an account? <a href="/register">Sign up</a></p>
      </div>
    </div>
  );
};

export default Login;
